// Récupération des données depuis le contexte global
var temp = global.get("current_temperature") || 20;
var humidity = global.get("current_humidity") || 50;
var light = global.get("current_light") || 500;
var windSpeed = global.get("current_wind_speed") || 0;
var airQuality = global.get("current_air_quality") || 400;

// Détermination des conditions météo
var weatherCondition = "";
var weatherEmoji = "";
var weatherDesc = "";

if (temp > 30 && light > 800) {
    weatherCondition = "sunny";
    weatherEmoji = "☀️";
    weatherDesc = "Ensoleillé";
} else if (humidity > 80 && temp < 15) {
    weatherCondition = "rainy";
    weatherEmoji = "🌧️";
    weatherDesc = "Pluvieux";
} else if (temp < 5) {
    weatherCondition = "snowy";
    weatherEmoji = "❄️";
    weatherDesc = "Neigeux";
} else if (windSpeed > 30) {
    weatherCondition = "windy";
    weatherEmoji = "💨";
    weatherDesc = "Venteux";
} else if (light < 200) {
    weatherCondition = "cloudy";
    weatherEmoji = "☁️";
    weatherDesc = "Nuageux";
} else {
    weatherCondition = "partly_cloudy";
    weatherEmoji = "⛅";
    weatherDesc = "Partiellement nuageux";
}

// Message de sortie pour l'affichage
msg.payload = {
    condition: weatherCondition,
    emoji: weatherEmoji,
    description: weatherDesc,
    temperature: temp,
    humidity: humidity,
    light: light,
    windSpeed: windSpeed,
    airQuality: airQuality,
    timestamp: new Date().toISOString()
};

return msg;

// Extraction de la valeur du capteur
var value = msg.payload.value || msg.payload;

// Sauvegarde dans le contexte global
global.set("current_temperature", value);

// Transmission pour affichage et analyse
msg.payload = {
    sensor: "temperature",
    value: value,
    timestamp: new Date().toISOString()
};

return msg;

