// Récupération des valeurs actuelles
var temp = global.get("current_temperature") || 20;
var humidity = global.get("current_humidity") || 50;
var windSpeed = global.get("current_wind_speed") || 0;
var airQuality = global.get("current_air_quality") || 400;

var alertActive = false;
var alertMsg = null;
var actionMsgs = [];

// Condition d'alerte: temp > 35
if (temp > 35) {
    alertActive = true;

    alertMsg = {
        payload: {
            type: "heatwave",
            emoji: "🔥",
            message: "CANICULE DÉTECTÉE",
            value: temp,
            active: true,
            timestamp: new Date().toISOString(),
            priority: "high"
        }
    };

    // Actions automatiques
    actionMsgs.push({topic: "station/control/fan", payload: "ON"});
actionMsgs.push({topic: "station/control/led", payload: "HEATWAVE"});
}

// Retour des messages
var outputs = [alertMsg];
for (var i = 0; i < 2; i++) {
    outputs.push(actionMsgs[i] || null);
}

return outputs;

