var sensor = msg.payload.sensor;
var value = msg.payload.value;
var timestamp = new Date().toISOString().slice(0, 19).replace('T', ' ');

var sql = "INSERT INTO sensor_data (timestamp, " + sensor + ") VALUES (?, ?) ON DUPLICATE KEY UPDATE " + sensor + " = VALUES(" + sensor + ");";
var params = [timestamp, value];

msg.topic = sql;
msg.payload = params;

return msg;

if (msg.payload && msg.payload.active) {
    var alertType = msg.payload.type;
    var value = msg.payload.value;
    var message = msg.payload.message;
    var timestamp = msg.payload.timestamp;

    var sql = "INSERT INTO alerts (timestamp, alert_type, value, message, priority) VALUES (?, ?, ?, ?, ?)";
    var params = [timestamp, alertType, value, message, msg.payload.priority || 'medium'];

    msg.topic = sql;
    msg.payload = params;

    return msg;
} else {
    return null;
}

var value = msg.payload.value;
var timestamp = new Date(msg.payload.timestamp).getTime();

var history = global.get('wind_speedHistory') || [];
history.push({x: timestamp, y: value});

// Limiter l'historique à 100 points
if (history.length > 100) {
    history.shift();
}
global.set('wind_speedHistory', history);

msg.payload = history;
return msg;

global.set('tempHistory', []);
global.set('humidityHistory', []);
global.set('lightHistory', []);
global.set('airQualityHistory', []);
global.set('windSpeedHistory', []);
return null; // Ce nœud n'a pas besoin d'envoyer de message

// Récupérer l'historique des températures
var history = global.get('tempHistory') || [];

// Créer un message pour le graphique
msg.payload = [{
    series: ["Température"],
    data: [history.map(function(point) {
        return {x: point.x, y: point.y};
    })],
    labels: [""]
}];

return msg;

