    msg.payload = { topic: "arduino/led/day", payload: 1 }; // LED Jour ON
    flow.set("is_day", true);
    return [msg, { topic: "arduino/led/night", payload: 0 }]; // LED Nuit OFF
} else {
    msg.payload = { topic: "arduino/led/night", payload: 1 }; // LED Nuit ON
    flow.set("is_day", false);
    return [{ topic: "arduino/led/day", payload: 0 }, msg]; // LED Jour OFF
}

var alert_active = flow.get('alert_active') || false;
